    ["ll_mexican_beer"] = math.random(20, 30),
    ["ll_tequila"] = math.random(30, 40),
    ["ll_cointreau"] = math.random(20, 40),	
    ["ll_dryvermounth"] = math.random(30, 40),
    ["ll_margarita"] = math.random(20, 40),	
    ["ll_chelada"] = math.random(30, 40),
}



